SELECT ID, SUM AS NUM FROM (select ID,SUM(NUM) SUM From ((select accepter_id ID ,count(accepter_id) NUM  from RequestAccepted  group by accepter_id )   UNION ALL (select requester_id ID,count(requester_id) NUM  from RequestAccepted group by requester_id)) group by ID ) X WHERE  SUM=(SELECT Max(SUM )from (select ID,SUM(NUM) SUM From ((select accepter_id ID ,count(accepter_id) NUM  from RequestAccepted  group by accepter_id )   UNION ALL (select requester_id ID,count(requester_id) NUM  from RequestAccepted group by requester_id)) group by ID ) X)